mapper.select:


SELECT no, title, contents, reg_date AS regDate, category_no AS categoryNo
FROM post
WHERE category_no = #{no };



SELECT title, contents, reg_date AS regDate
FROM post
WHERE category_no = #{no }
ORDER BY reg_date ASC
LIMIT 0,1;



SELECT no, title, contents, reg_date AS regDate, category_no AS categoryNo
FROM post
WHERE no = #{postNo } AND category_no = #{categoryNo };
